;(async () => {
  let output = ''
  for (const letter of letters) {
    let split = letter.split('\n');
    output += `case "":\n`
    output += `return []string{\n`
    for (const s of split) {
      output += '`'+s+'`,\n'
    }
    output += "}\n";
  }
  await fs.writeFile('./../generated.go', output, { encoding: 'utf-8' });
})();